        uint256 liquidationProtocolFeeRate,
        uint256 liquidatorFeeRate,
        address uniswapRouter,
        address uniswapQuoter
    ) external;

    function deposit(bytes32 ccy, uint256 amount) external payable;

    function depositFrom(
        address user,
        bytes32 ccy,
        uint256 amount
    ) external payable;

