    function addDepositAmount(
        address user,
        bytes32 ccy,
        uint256 amount
    ) external;

    function removeDepositAmount(
        address user,
        bytes32 ccy,
        uint256 amount
    ) external;

    function swapDepositAmounts(
        address liquidator,
        address user,
        bytes32 ccyFrom,
        bytes32 ccyTo,
        uint256 amountOut,
        uint24 poolFee,
        uint256 offsetAmount
    ) external returns (uint256 amountIn);
}
